mapper.select:
SELECT

FROM oomall_goods

    and id = #{id} 
    and goods_sn = #{goodsSn} 
    and name = #{name} 
    and category_id = #{categoryId} 
    and brand_id = #{brandId} 
    and be_onsale = #{beOnsale} 
    and be_deleted = #{beDeleted}

mapper.update:
UPDATE oomall_goods
and goods_sn = #{goodsSn}, and name = #{name}, and category_id = #{categoryId}, and brand_id = #{brandId}, and brief = #{brief}, and spec_list = #{specList}, and spec_url = #{picUrl}, and unit = #{unit}, and be_onsale = #{beOnsale}, update_time = CURRENT_TIME, modi_user = #{modiUser}
WHERE id = #{id}

UPDATE oomall_goods
SET be_deleted = true
WHERE id = #{id}

UPDATE oomall_product
SET be_deleted = true
WHERE goods_id = #{id}

UPDATE oomall_product
SET be_deleted = true
WHERE id = #{id}
